import { LogManager } from './lib/logManager'
import { ILogger, Logger, ILoggerImplementationOptions } from './lib/logger'
import { EstablishKinesisStreamOptions } from './lib/core'
import { getConsoleStream } from './lib/consoleStream'

const logManager = new LogManager()
export function getLogger(
  appName: string,
  serviceName: string,
  context?: any,
  options?: ILoggerImplementationOptions) {
  return logManager.getLogger(appName, serviceName, context, options)
}
export function enablePostBunyanTransform() {
  Logger.enablePostBunyanTransform = true
}
export { EstablishKinesisStreamOptions, ILogger, ILoggerImplementationOptions, getConsoleStream }
