/* eslint-env node, mocha */
'use strict'
var chai = require('chai')
var expect = chai.expect
var DebugStream = require('../../dist/lib/debug').default

describe('DebugStream', function () {
  it('should export DebugStream class', function () {
    expect(DebugStream).to.be.a('function')
  })
  it('should export write function', function () {
    expect(DebugStream.prototype.write).to.be.a('function')
    expect(() => DebugStream.prototype.write.call('abc'))
      .not.to.throw
    expect(() => {
      let stream = new DebugStream()
      stream.write('abc')
    }).not.to.throw
  })
})
