/* eslint-env node, mocha */
'use strict'
var chai = require('chai')
var expect = chai.expect
var index = require('../../dist/index')
var Logger = require('../../dist/lib/logger').Logger

describe('getLogger()', function () {
  it('should return a new Logger', function () {
    var l = index.getLogger('a', 's')
    expect(l).to.be.instanceOf(Logger)
  })
  it('should return the same Logger', function () {
    var l = index.getLogger('a', 's')
    var l1 = index.getLogger('a', 's')
    expect(l).to.be.equal(l1)
  })
  it('should return diff Loggers', function () {
    var l = index.getLogger('a', 's')
    var l1 = index.getLogger('b', 's')
    expect(l).to.not.be.equal(l1)
  })
})
describe('enablePostBunyanTransform()', function () {
  it('should set Logger.enablePostBunyanTransform', function () {
    index.enablePostBunyanTransform()
    expect(Logger.enablePostBunyanTransform).to.be.equal(true)
  })
})
