'use strict'

//* *****************************************************************************
//* DEPENDENCIES
//* *****************************************************************************

const gulp = require('gulp')
const tsc = require('gulp-typescript')
const tslint = require('gulp-tslint')
const sourcemaps = require('gulp-sourcemaps')
const mocha = require('gulp-mocha')
const istanbul = require('gulp-istanbul')
const remapIstanbul = require('remap-istanbul/lib/gulpRemapIstanbul')
const open = require('gulp-open')
const del = require('del')
const gutil = require('gulp-util')

const paths = {
  ts: [
    'src/**/**.ts',
    'test/**/**.test.ts'
  ],
  src: ['src/**/*.ts'],
  srcjs: ['dist/src/**/*.js'],
  testts: ['test/**/*.test.ts'],
  testjs: ['dist/test/**/*.test.js']
}

let isWatching = false
const watching = function (cb) {
  gutil.log('WATCHING...')
  isWatching = true
  cb()
}
const handleError = function (task, doNotEnd) {
  return function (err) {
    gutil.log(gutil.colors.blue(task), gutil.colors.red(err))
    if (!doNotEnd) {
      this.emit('end')
    }
  }
}
const wrap = function (stream, task, doNotEnd) {
  gutil.log(gutil.colors.blue('wrapping ', task))
  return isWatching ? stream.on('error', handleError(task, doNotEnd)) : stream.on('error', function (err) {
    gutil.log(gutil.colors.blue(task), gutil.colors.red(err))
    process.exit(1)
  })
}

//* *****************************************************************************
//* LINT
//* *****************************************************************************
gulp.task('lint', function () {
  let config = { formatter: 'verbose', emitError: !process.env.CI }
  return wrap(gulp.src(paths.ts)
    .pipe(tslint(config))
    .pipe(tslint.report()), 'lint')
})

//* *****************************************************************************
//* BUILD
//* *****************************************************************************
let tsLibProject = tsc.createProject('tsconfig.json', {
  module: 'commonjs',
  target: 'es5',
  typescript: require('typescript')
})

gulp.task('build-lib', function () {
  return wrap(gulp.src([
    'src/**/*.ts'
  ])
    .pipe(tsLibProject())
    // .on('error', function () {
    //   process.exit(1)
    // })
    .js.pipe(gulp.dest('lib/')))
})

let tsEsProject = tsc.createProject('tsconfig.json', { module: 'es2015', typescript: require('typescript') })

gulp.task('build-es', function () {
  return wrap(gulp.src(paths.src)
    .pipe(tsEsProject())
    .js.pipe(gulp.dest('es/')), 'build-es')
})

let tsDtsProject = tsc.createProject('tsconfig.json', {
  declaration: true,
  noResolve: false,
  typescript: require('typescript')
})

gulp.task('build-dts', gulp.series(function () {
  return wrap(gulp.src(paths.src)
    .pipe(tsDtsProject())
    .dts.pipe(gulp.dest('dts')), 'build-dts')
}, function () {
  return gulp.src(['src/**/*.d.ts'])
    .pipe(gulp.dest('dts'))
}))

//* *****************************************************************************
//* TESTS NODE
//* *****************************************************************************
let tstProject = tsc.createProject('tsconfig.json', {
  typescript: require('typescript')
})

gulp.task('build-src', function () {
  return wrap(gulp.src(paths.src)
    .pipe(sourcemaps.init()) // This means sourcemaps will be generated
    .pipe(tstProject())
    // .on('error', function () {
    //   process.exit(1)
    // })
    .js
    .pipe(sourcemaps.write()) // Now the sourcemaps are added to the .js file
    .pipe(gulp.dest('dist/src')), 'build-src')
})

let tsTestProject = tsc.createProject('tsconfig.json', {
  typescript: require('typescript'),
  allowJs: true
})

gulp.task('build-test', function () {
  return wrap(gulp.src(paths.testts)
    .pipe(tsTestProject())
    // .on('error', function (err) {
    //   process.exit(1)
    // })
    .js.pipe(gulp.dest('dist/test')), 'build-test')
})

gulp.task('istanbul:hook', function () {
  return gulp.src(paths.srcjs)
    .pipe(istanbul())
    .pipe(istanbul.hookRequire())
})

const remap = function () {
  return gulp.src('coverage/coverage-final.json')
    .pipe(remapIstanbul({
      reports: {
        'json': 'coverage/coverage.json',
        'html': 'coverage/html-report'
      }
    }))
}
gulp.task('remap-istanbul', remap)

gulp.task('open-report', gulp.series(function () {
  return gulp.src(['coverage/*/index.html'])
    .pipe(open())
}))

let reporters = ['lcovonly', 'json', 'text', 'text-summary']

const test = function () {
  return wrap(gulp.src(paths.testjs)
    .pipe(wrap(mocha(), 'mocha'))
    .pipe(istanbul.writeReports({
      reporters: reporters
    }))
    .pipe(istanbul.enforceThresholds({
      thresholds: {
        global: {
          statements: 100,
          branches: 85,
          lines: 100,
          functions: 100
        }
      }
    })), 'test', true)
}

gulp.task('mocha', gulp.series('istanbul:hook', test))
function testjsmode(done) {
  reporters.push('html')
  done()
}
//* *****************************************************************************
//* CLEAN
//* *****************************************************************************
gulp.task('clean-tsc', gulp.series(() => del(['coverage', 'dist', 'dts', 'lib', 'es'])))
//* *****************************************************************************
//* DEFAULT
//* *****************************************************************************
gulp.task('build', gulp.series(gulp.parallel('clean-tsc', 'lint'), gulp.parallel('build-lib', 'build-src', 'build-test', 'build-dts', 'build-es')))
gulp.task('test', gulp.series('clean-tsc', gulp.parallel('build-src', 'build-test'), 'mocha', 'remap-istanbul'))
gulp.task('testjs', gulp.series('clean-tsc', testjsmode, gulp.parallel('build-src'), 'mocha'))
gulp.task('test-coverage', gulp.series(watching, 'clean-tsc', gulp.parallel('build-src', 'build-test'), 'mocha', 'remap-istanbul', 'open-report'))
gulp.task('testjs-coverage', gulp.series(watching, testjsmode, 'clean-tsc', gulp.parallel('build-src', 'build-test'), 'mocha', 'open-report'))
gulp.task('default', gulp.series('test'))

//* *****************************************************************************
//* WATCH
//* *****************************************************************************

const watch = function () {
  wrap(gulp.watch(paths.ts, gulp.series('test')), 'watch')
}
gulp.task('watch', gulp.series(watching, 'test', watch))
