/// <reference types="mocha" />

import { expect } from 'chai'
import * as aws from '../src/aws'
import * as Queues from '../src/aws/queues'

describe('infrastructure-node-cloudservices-lib/aws)', function () {
  it('should export the Queues module', function () {
    expect(aws.Queues).to.be.ok
  })
  describe('/aws/queues', function () {
    it('should export the SQSQueueClient class', function () {
      expect(Queues).to.be.ok
      expect(aws.Queues.SQSQueueClient).to.be.ok
    })
  })
  describe('/aws/lambda', function () {
    it('should export the LambdaHandler abstract class', function () {
      expect(aws.Lambda.LambdaHandler).to.be.ok
    })
  })
})
